CREATE TABLE public.direction
(
    id integer DEFAULT nextval('direction_id_dir_seq'::regclass) PRIMARY KEY NOT NULL,
    name varchar(100),
    id_uni integer,
    info varchar(1000),
    budget_places integer,
    CONSTRAINT direction_university_id_fk FOREIGN KEY (id_uni) REFERENCES public.university (id)
);
CREATE UNIQUE INDEX direction_id_dir_uindex ON public.direction (id);

