INSERT INTO public.direction (id, name, id_uni, info, budget_places) VALUES (20, 'Энергетика', 9, 'Высоковольтные электроэнергетика и электротехника; Нетрадиционные и возобновляемые источники энергии; Релейная защита и автоматизация электроэнергетических систем; Электрические станции; Электроэнергетические системы и сети; Гидроэлектростанции; Электроснабжение; Электромеханика; Электрические и электронные аппараты; Менеджмент в электроэнергетике и электротехнике; Техногенная безопасность в электроэнергетике и электротехнике', 50);
INSERT INTO public.direction (id, name, id_uni, info, budget_places) VALUES (16, 'Теология', 10, 'Государственное учреждение образования «Институт теологии имени святых Мефодия и Кирилла» Белорусского государственного университета создан в октябре 2004 года как юридически самостоятельное структурное подразделение ведущего высшего учебного заведения республики.', 1);
INSERT INTO public.direction (id, name, id_uni, info, budget_places) VALUES (11, 'Прикладная математика', 8, 'Факультет был основан в 1970 году. Самим фактом своего создания, выработкой структуры и основных направлений научной деятельности факультет ВМК целиком обязан одному из крупнейших учёных России — академику Андрею Николаевичу Тихонову. Усилия А.Н. Тихонова по созданию факультета ВМК получили поддержку академика М.В. Келдыша — в то время президента Академии наук СССР. ', 40);
INSERT INTO public.direction (id, name, id_uni, info, budget_places) VALUES (13, 'Вычислительная математика и кибернетика', 8, 'Факультет вычислительной математики и кибернетики (ВМК) Московского государственного университета имени М.В.Ломоносова является ведущим учебным центром России по подготовке кадров в области фундаментальных исследований по прикладной математике, вычислительной математике, информатике и программированию.', 40);
INSERT INTO public.direction (id, name, id_uni, info, budget_places) VALUES (15, 'Физика', 8, 'За плечами физического факультета почти два с половиной века. И это не просто годы и даты. Это дела и судьбы многих тысяч людей, выдающихся ученых, исследователей, талантливых педагогов. Это история мировых открытий и Нобелевских премий. Это Alma Mater русской физики и русской физической школы.
INSERT INTO public.direction (id, name, id_uni, info, budget_places) VALUES (14, 'Химия и материалы', 10, 'Химический факультет (основан в 1963 г.) ведущий факультет Башгосуниверситета, единственное место в     Республике Башкортостане, где готовят бакалавров, специалистов и магистров в совершенстве владеющих фундаментальными и прикладными химическими знаниями.', 30);
INSERT INTO public.direction (id, name, id_uni, info, budget_places) VALUES (21, 'Фундаментальная алгебра', 11, 'В настоящем курсе детально обсуждаются как основные понятия и важнейшие факты общей теории групп, так и несколько более глубоких результатов, относящихся к арифметическим свойствам и структуре конечных групп, заданию групп образующими и соотношениями и действиям групп.', 70);
INSERT INTO public.direction (id, name, id_uni, info, budget_places) VALUES (19, 'Фармацевтика', 11, 'Химико-технологический институт CПБГУ — яркий пример успешного взаимодействия добрых традиций старой школы и последних разработок в области науки и образования. «Все оборудование, которое есть в ХТИ, установлено в первую очередь для того, чтобы студенты с первого курса осознавали, что такое наука»', 20);
INSERT INTO public.direction (id, name, id_uni, info, budget_places) VALUES (17, 'Программная инженерия', 12, 'Выдающийся коллектив преподавателей и научных сотрудников, который обеспечивает подготовку практически во всех направлениях современной информатики и информационных технологий', 25);
INSERT INTO public.direction (id, name, id_uni, info, budget_places) VALUES (12, 'Биологические науки', 7, 'ИФМиБ – один из самых динамично развивающихся институтов КФУ, в составе которого более 60 подразделений (кафедры, научно-исследовательские лаборатории, клиническая база в Республиканской клинической больнице № 2, музеи, ботанический сад и многое другое). ', 25);
INSERT INTO public.direction (id, name, id_uni, info, budget_places) VALUES (9, 'Прикладная информатика', 7, 'Институт вычислительной математики и информационных технологий КФУ - один из ведущих российских образовательных и исследовательских центров в области вычислительной и прикладной математики, а также фундаментальной информатики и информационных технологий.  Узнать больше об институте.', 70);
INSERT INTO public.direction (id, name, id_uni, info, budget_places) VALUES (18, 'Прикладная математика', 7, 'Кафедра прикладной математики обеспечивает преподавание общих и специальных дисциплин для бакалавров, магистрантов и аспирантов Института вычислительной математики и информационных технологий, проводит специализацию по профилю «Математическое моделирование» и выполняет научные исследования в области математического моделирования и математической физики.', 40);
INSERT INTO public.direction (id, name, id_uni, info, budget_places) VALUES (10, 'Программная инженерия', 7, 'Кафедра программной инженерии формирует образовательную программу Высшей школы ИТИС. Образовательные траектории строятся в соответствии со сводом знаний по программной инженерии (The Guide to the Software Engineering Body of Knowledge, SWEBOK) и мировому стандарту по подготовке программных инженеров (Computer Science Curricula).', 10);